mapper.update:
UPDATE file SET
  User = #{userId},
  Time = #{lastWriteTime},
  FileSize = #{fileLength},
  Extension = #{extension}
WHERE ID = #{roomId} AND FileName = #{saveFileName};

UPDATE file SET
  User = #{userId},
  FileName = #{newFileName},
  Extension = #{newExtension}
WHERE ID = #{roomId} AND fileName = #{oldFileName} AND Extension = #{oldExtension}

mapper.select:
SELECT FileName FROM file WHERE FileName
NOT IN

  #{item}

AND ID = #{roomId}

SELECT FileName FROM file WHERE FileName
IN

    #{item}

AND ID = #{roomId}
